length = 0
stack_arr = [[0, 0] for _ in s]
for i, e in enumerate(s):
    if e == '(':
        for j in range(i + 1):
            stack_arr[j][1] += 1
            stack_arr[j][0] += 1
    else:
        for j in range(i + 1):
            if stack_arr[j][1] == 0:
                stack_arr[j][1] = float('-inf')
